import { useState, useEffect } from "react";
import styles from "./Card.module.scss";
import CardTitle from "../../atoms/CardTitle";
import CardImage from "../../atoms/CardImage";
import CardDescription from "../../atoms/CardDescription";
import ThemeToggle from "../../atoms/ThemeToggle";

/* 
    * Card Component
    @param {Object} props - Component properties
    @param {string} props.image - URL of the card image
    @param {string} props.title - Title of the card
    @param {string} props.description - Description of the card
    @param {Function} props.onButtonClick - Callback function for button click
    @returns {JSX.Element} Rendered Card component
 */
export default function Card({ image, title, description, onButtonClick }) {
  // Inicializa desde localStorage o 'light'
  const [isDark, setIsDark] = useState(
    () => localStorage.getItem("theme") === "dark"
  );

  // Al cambiar, actualiza <body> y guarda en localStorage
  useEffect(() => {
    document.body.classList.toggle("dark-theme", isDark);
    localStorage.setItem("theme", isDark ? "dark" : "light");
  }, [isDark]);

  const handleToggle = () => setIsDark((prev) => !prev);
  return (
    <div className={styles.card}>
      <CardImage src={image} alt={title} />
      <div className={styles.content}>
        <CardTitle>{title}</CardTitle>
        <CardDescription>{description}</CardDescription>
        <ThemeToggle isDark={isDark} onToggle={handleToggle} />
      </div>
    </div>
  );
}
