import React, { useState, useEffect } from "react";
import ThemeToggle from "./components/atoms/ThemeToggle";
import Card from "./components/molecules/Card";
import sampleImg from "./assets/imgs/undraw_delivery-location_um5t.png";

function App() {
  // Inicializa desde localStorage o 'light'
  const [isDark, setIsDark] = useState(
    () => localStorage.getItem("theme") === "dark"
  );

  // Al cambiar, actualiza <body> y guarda en localStorage
  useEffect(() => {
    document.body.classList.toggle("dark-theme", isDark);
    localStorage.setItem("theme", isDark ? "dark" : "light");
  }, [isDark]);

  const handleToggle = () => setIsDark((prev) => !prev);

  return (
    <div
      style={{
        padding: "2rem",
        display: "grid",
        gap: "2rem",
        justifyContent: "center",
      }}>
      <ThemeToggle isDark={isDark} onToggle={handleToggle} />

      <Card
        image={sampleImg}
        title="Atomic Card"
        description="Esta es una tarjeta de ejemplo usando Atomic Design y Sass."
        onButtonClick={() => alert("¡Clic!")}
      />
    </div>
  );
}

export default App;
